import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import './Home.css';

const divStyle = {
  width: '100%',
  height: '100px',
  backgroundColor: `blue`,
  backgroundSize: 'cover'  
};

class Home extends React.Component {
  state = {
    redirect: false
  }

  componentDidMount() {
    this.id = setTimeout(() => this.setState({ redirect: true }),27000)
  }

  componentWillUnmount() {
    clearTimeout(this.id)
  }

  render() {
    return this.state.redirect
      ? <Redirect to="/gallery" />
      : <div>
        <div  style={divStyle}><h1 align="center">Welcome to Digidarpan</h1>   
    
      </div>
      <div className="">
       <h2 align="center">Player id :0000-0000-954f-86b6</h2>  
      <p align="center">Register your Player id at signage.digidarpan.com to manage</p>
      <p align="center">Connectivity Ethernet :UP ,DNS:OK, PING : FAIL,DHCP : 192.168.0.1</p>
      <p align="center">Server:signage.digidarpan.com,Status:Connected</p>
        
      <p align="center">For any queries please contact:+91-9900884823</p>
       </div>
       </div>
    
  }
}

export default Home;
