import React, {Component} from 'react';
import Fullscreen from "react-full-screen";
import './Welcome.css';
import ReactDOM from 'react-dom';
import axios from 'axios';

class Welcome extends React.Component {
  state = {
    assets: []
  }

  componentDidMount() {
    fetch('./play.json')
    .then(res => res.json())
    .then((data) => {
      this.setState({ assets: data  })
   
    })
    .catch(console.log)
  }
 render() {
const data = this.state.assets.data && this.state.assets.data.assets ? this.state.assets.data.assets :[];
     console.log(this.state);
    return (
  
       <div className="container">
    
         {data.map((asset , index) => (
         
            <span key={asset.id}>
    
 
     
        <video 
        height="100%" width="100%" 
        autoPlay 
        loop style={{position:"absolute"}} controls >
       <source src={asset.filename[0]} type="video/mp4"/> 
        
     </video>
       <div class="row"><img src={asset.filename} height="20%" width="40%" /></div>
  </span>
            
    
        ))}
      
       </div>

    );
  }
}
export default Welcome;
