import React, {Component} from 'react';
import Fullscreen from "react-full-screen";
import './Welcome.css';
import ReactDOM from 'react-dom';
import axios from "axios";


class Welcome extends Component {
  constructor(props) {
    super(props);

    this.state = {
      assets: []
    };
  }



  componentDidMount() {
    axios.get('/play.json')
   .then(res => res.json())
    .then((data) => {
      this.setState({ assets: data  })
    })
  }



// Object.keys is used to map through the data. Can't map through the data without this because the data is not an array. Map can only be used on arrays.
  render() {
    console.log(this.state.assets.data);
    return (
      <div className="App">
        {Object.keys(this.state.assets.data).map((key) => (
          <div className="container">
            <span className="left">{key}</span>
            <span className="right"><span value={this.state.assets[key].filename}></span></span>
          </div>
        ))}
      </div>
    );
  }
}

export default Welcome;
