import React, {Component} from 'react';
import Fullscreen from "react-full-screen";
import './Welcome.css';
import ReactDOM from 'react-dom';
import axios from 'axios';

class Welcome extends React.Component {
  state = {
    assets: [],
       activeMediaIndex: 0
  }

  componentDidMount() {
    fetch('./play.json')
    .then(res => res.json())
    .then((data) => {
      this.setState({ assets: data  })
   
    })
    .catch(console.log)
       setInterval(this.changeActiveAsset.bind(this), 5000);
  }
 changeActiveMedia() {
    const assetsLength = this.state.a.length;
    let nextAssetIndex = this.state.activeAssetIndex + 1;

    if(nextAssetIndex >= assetsLength) {
      nextAssetIndex = 1;
    }

    this.setState({ activeAssetIndex:nextAssetIndex });
  }

  renderSlideshow(){
    const ad = this.state.assets[this.state.activeAssetIndex];
    let adType = ad.adType;
    if(adType.includes("image")){
      return(
        <div className="imagePreview">
          <img src={ad.filename} />
        </div>
      );
    }else if (adType.includes("video")){
      return(
        <video className="videoPreview" controls>
            <source src={ad.filename} type={adType}/>
          Your browser does not support the video tag.
        </video>
      )

    }
  }

  render(){   
    return(
      <div>
          {this.renderSlideshow()}
      </div>
    )
  }
}
export default Welcome;
