       <div className="container">
    
         {data.map((asset , index) => (
         
            <span key={asset.id}>
    
 
     
       
       <div className="row">
       <div className="col-xs-2"> <img src={asset.filename} height="20%" width="40%" />
        </div>
        </div>
  </span>
            
    
        ))}
      
       </div>

    );
  }
}
export default Welcome;
