import React, {Component} from 'react';
import Fullscreen from "react-full-screen";
import './Welcome.css';
import ReactDOM from 'react-dom';
import axios from 'axios';

class Welcome extends React.Component {
     constructor(props) {
    super(props);
    this._video = null;
    this._fullScreenActive = null;
    this._fullScreenIntervalSeconds = 5;
    this.state = {
      fullScreen: false,
      playing: false,
      showSettings: false,
      dur: "00:00:00",
      progress: "0"
    };
  }
  state = {
    assets: []
  }

  componentDidMount() {
    fetch('./play.json')
    .then(res => res.json())
    .then((data) => {
      this.setState({ assets: data  })
   
    })
    .catch(console.log)
  }
 render() {
const data = this.state.assets.data && this.state.assets.data.assets ? this.state.assets.data.assets :[];
     console.log(this.state);
    return (
  
       <div className="container">
        <div className="col-xs-12">
       
         {data.map((asset , index) => (
          <div className="card">
            <div className="card-body">
            <table key={data.id}>
      <tr>
   <img src={asset.filename}/>
     
        <video controls 
        autoPlay 
        loop >
       <source src={asset.filename} type="video/mp4"/> 
     </video>
   </tr>     
  </table>
            </div>
          </div>
    
        ))}
        </div>
       </div>

    );
  }
}
export default Welcome;
